key = {chr(i + 65): format(i + 1, '05b') for i in range(26)}

crypt = input("文字列を入力: ").upper()

output = ""


for char in crypt:
    if char in key:
        output += key[char]
    else:
        print(f"無効な文字が含まれています: {char}")
        break

def compare_bits(output):
    key = ""
    result = ""

    for i in range(len(output)):
        if output[i] != key[i]:
            result += "0"
        else:
            result += "1"

    return result

shuturyoku = compare_bits(output)
print("結果:", shuturyoku)
